<script>
export default {
  created() {
    let type = sessionStorage.getItem("user");
    type = type.split("/");
    if (type[2] === "0") {
      this.isShow = false;
    } else {
      this.isShow = true;
    }
    switch (this.$route.path) {
      case "/userInfo": {
        this.active = "userInfo";
        break;
      }
      case "/myOrder": {
        this.active = "myOrder";
        break;
      }
      case "/goodsManager": {
        this.active = "goodsManager";
        break;
      }
      case "/userList":
      case "/userAdd":
      case "/userModify": {
        this.active = "userList";
        break;
      }
      case "/sort": {
        this.active = "sort";
        break;
      }
      case "/goodsAdd": {
        this.active = "goodsAdd";
        break;
      }
      case "/newsList": {
        this.active = "newsList";
        break;
      }
      case "/allOder": {
        this.active = "allOder";
        break;
      }
      case "/LeavingBook": {
        this.active = "LeavingBook";
        break;
      }
    }
  },
  data() {
    return {
      active: "myOrder",
      isShow: false
    };
  },
  methods: {}
};
</script>
